import {useEffect, useState} from 'react';

import classes from "./AvailableMeals.module.css";
import Card from "../UI/Card";
import MealItem from "./MealItem/MealItem";


const AvailableMeals = () => {

  const[meals, setMeals]=useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [httpError, setHttpError] = useState(null);

  useEffect (() => {
    const fetchMeals = async()=> {
      const response = await fetch('https://react---http-c4060-default-rtdb.firebaseio.com/meals.json');

      if (!response.ok) {
        throw new Error('This Page Is Not Available Right Now.This may be because of a technical error that we are working to get fixed. Try reloading this page.')
      }

      const responseData = await response.json();

      const loadedMeals = [];
      for (const key in responseData) {
        loadedMeals.push({
          id:key,
          name:responseData[key].name,
          description:responseData[key].description,
          price:responseData[key].price,
        });
      }
      setMeals(loadedMeals);
      setIsLoading(false);
    };
    fetchMeals().catch((error)=>  {
      setIsLoading(false);
      setHttpError(error.message);
    });
  },[])

  if (isLoading) {
    return (
    <section className={classes.mealsLoading}>
      <p>Loading...</p>
    </section>
    )
  }  

  if (httpError) {
    return (
      <section className={classes.mealsError}>
        <h1>This Page Is Not Available Right Now</h1>
        <p>This may be because of a technical error that we are working to get fixed.</p>
        <p>Try reloading this page.</p>
      </section>
    )
  }

  const mealsList = meals.map((meal) => (
    <MealItem
      id = {meal.id}
      key={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
    />
  ));

  return (
    <section className={classes.meals}>
      <Card>
        <ul>{mealsList}</ul>
      </Card>
    </section>
  );
};

export default AvailableMeals;
